mapper.select:
select  from ts_main_task
 where 1=1

  and 1=1


  and UNIX_TIMESTAMP(create_date) >= (#{startTime, jdbcType=BIGINT} / 1000) + 8 * 60 * 60


  and UNIX_TIMESTAMP(create_date) <= (#{endTime, jdbcType=BIGINT} / 1000) + 8 * 60 * 60


  and main_task_num = #{mainTaskNum}


  and main_task_type_code = #{mainTaskTypeCode}


  and task_status = #{taskStatus}


  and area_code = #{areaCode}

order by create_date desc, task_status asc

select  from ts_main_task
where main_task_num = #{mainTaskNum}
LIMIT 1

select
from ts_main_task where task_status in
#{item}

mapper.update:
update ts_main_task
source_floor = #{sourceFloor}, dest_floor = #{destFloor}, elevator_work_type = #{elevatorWorkType},
where main_task_num = #{mainTaskNum}
